CREATE TABLE IF NOT EXISTS migrations (id integer not null primary key, migration varchar(40) not null, batch integer not null);
CREATE TABLE IF NOT EXISTS password_resets (email varchar(40) not null, token varchar(40) not null, created_at datetime);
CREATE TABLE IF NOT EXISTS failed_jobs (id integer not null primary key, uuid varchar(40) not null, connection text not null, queue text not null, payload text not null, exception text not null, failed_at datetime default CURRENT_TIMESTAMP not null);
CREATE TABLE IF NOT EXISTS personal_access_tokens (id integer not null primary key, tokenable_type varchar(40) not null, tokenable_id integer not null, name varchar(40) not null, token varchar(40) not null, abilities text, last_used_at datetime, created_at datetime, updated_at datetime);
CREATE TABLE IF NOT EXISTS meetings (id varchar(40) not null, title varchar(40) not null, photoName varchar(40), body text, dateTime datetime not null, created_at datetime, updated_at datetime, lecture_id varchar(15), list_id varchar(15), primary key (id));
CREATE TABLE IF NOT EXISTS lectures (id varchar(40) not null, title varchar(40) not null, photoName varchar(40), created_at datetime, updated_at datetime, list_id varchar(40), primary key (id));
CREATE TABLE IF NOT EXISTS listsUsers (id integer not null primary key, title varchar(40) not null, creator varchar(40) not null, emails_list varchar(40) not null, created_at datetime, updated_at datetime);
CREATE TABLE IF NOT EXISTS list_users (id integer not null primary key, title varchar(40) not null, creator varchar(40) not null, emails_list varchar(40) not null, created_at datetime, updated_at datetime);
CREATE TABLE users (id VARCHAR(255) NOT NULL, name VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, isTeacher BOOLEAN DEFAULT NULL, remember_token VARCHAR(255) DEFAULT NULL, created_at DATETIME DEFAULT NULL, updated_at DATETIME DEFAULT NULL, PRIMARY KEY(id));
CREATE TABLE IF NOT EXISTS user_lectures (id integer not null primary key, lecture_id varchar(40) not null, user_id varchar(40) not null, isTeacher tinyint(1) not null, created_at datetime, updated_at datetime, foreign key(lecture_id) references lectures(id), foreign key(user_id) references users(id));
DELETE FROM sqlite_sequence;
CREATE INDEX password_resets_email_index on password_resets (email);
CREATE UNIQUE INDEX failed_jobs_uuid_unique on failed_jobs (uuid);
CREATE INDEX personal_access_tokens_tokenable_type_tokenable_id_index on personal_access_tokens (tokenable_type, tokenable_id);
CREATE UNIQUE INDEX personal_access_tokens_token_unique on personal_access_tokens (token);
CREATE UNIQUE INDEX meetings_id_unique on meetings (id);
CREATE UNIQUE INDEX lectures_id_unique on lectures (id);
CREATE UNIQUE INDEX listsusers_id_unique on listsUsers (id);
CREATE UNIQUE INDEX list_users_id_unique on list_users (id);
CREATE UNIQUE INDEX users_email_unique ON users (email);
CREATE UNIQUE INDEX users_id_unique ON users (id);
COMMIT;
